import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useClusteringStore } from "@/lib/clustering-store";
import { clusteringParamsSchema, apiConfigSchema } from "../../../shared/schema";
import { z } from "zod";
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { Wifi, WifiOff, Download } from "lucide-react";
import IndustrySelector from "@/components/industry-selector";
import { Checkbox } from "@/components/ui/checkbox";

const parametersSchema = z.object({
  lambda: clusteringParamsSchema.shape.lambda,
  k: z.number().int().min(2).max(20),
  pca_dim: clusteringParamsSchema.shape.pca_dim,
  level_value: z.array(z.string()).min(1, "Chọn ít nhất 1 mã ngành"),
});

const apiSchema = z.object({
  endpoint: apiConfigSchema.shape.endpoint,
});

export default function ClusteringForm() {
  const { parameters, apiConfig, updateParameters, updateApiConfig, infoFile: storeInfoFile, results } = useClusteringStore();
  const { toast } = useToast();
  const [connectionStatus, setConnectionStatus] = useState<"connected" | "disconnected" | "checking">("disconnected");
  const [infoFile, setInfoFile] = useState<File | null>(null);
  const [showOnlyAvailable, setShowOnlyAvailable] = useState(false);
  const [availableIndustries, setAvailableIndustries] = useState<string[]>([]);

  const parametersForm = useForm<z.infer<typeof parametersSchema>>({
    resolver: zodResolver(parametersSchema),
    defaultValues: {
      lambda: parameters.lambda,
      k: Array.isArray(parameters.k) ? parameters.k[0] : parameters.k,
      pca_dim: parameters.pca_dim,
      level_value: Array.isArray(parameters.level_value) ? parameters.level_value : [],
    },
  });

  const apiForm = useForm<z.infer<typeof apiSchema>>({
    resolver: zodResolver(apiSchema),
    defaultValues: {
      endpoint: apiConfig.endpoint || "https://06d2eadb5d12.ngrok-free.app",
    },
  });

  const onParametersSubmit = (data: { lambda: number; k: number; pca_dim: number; level_value: string[] }) => {
    updateParameters({
      lambda: data.lambda,
      k: data.k,
      pca_dim: data.pca_dim,
      level_value: data.level_value,
    });

    // Show success notification
    toast({
      title: "Parameters Updated",
      description: "Algorithm parameters have been successfully updated",
      variant: "default",
    });

    // Log current parameter values
    console.log("✅ Parameters updated successfully:");
    console.log("📊 Current parameter values:");
    console.log({
      lambda: data.lambda,
      k: data.k,
      pca_dim: data.pca_dim,
      level_value: data.level_value,
    });
  };

  const onApiConfigSubmit = async (data: { endpoint: string }) => {
    updateApiConfig({
      endpoint: data.endpoint,
    });

    // Real connection check using /meta endpoint
    if (data.endpoint) {
      setConnectionStatus("checking");
      try {
        const { clusteringApi } = await import("@/lib/clustering-api");
        await clusteringApi.getMeta({ endpoint: data.endpoint });
        setConnectionStatus("connected");
      } catch (error) {
        console.error("Connection failed:", error);
        const errorMessage = error instanceof Error ? error.message : "Unknown connection error";
        toast({
          title: "Connection Failed",
          description: errorMessage,
          variant: "destructive",
        });
        setConnectionStatus("disconnected");
      }
    } else {
      setConnectionStatus("disconnected");
    }
  };

  const parseIndustriesFromCSV = async (file: File): Promise<string[]> => {
    try {
      const text = await file.text();
      const lines = text.split('\n');
      const industries = new Set<string>();

      if (lines.length === 0) return [];

      // Parse header to find sector_unique_id column
      const headerLine = lines[0].trim();
      const headers = headerLine.split(',').map(col => col.trim().replace(/"/g, ''));
      const sectorColumnIndex = headers.findIndex(header => 
        header.toLowerCase().includes('sector_unique_id') || 
        header.toLowerCase().includes('sector') ||
        header.toLowerCase().includes('industry_code') ||
        header.toLowerCase().includes('industrycode')
      );

      console.log("📋 CSV Headers:", headers);
      console.log("🎯 Found sector column at index:", sectorColumnIndex, headers[sectorColumnIndex]);

      // If sector_unique_id column found, use it specifically
      if (sectorColumnIndex !== -1) {
        for (let i = 1; i < lines.length; i++) {
          const line = lines[i].trim();
          if (!line) continue;

          const columns = line.split(',').map(col => col.trim().replace(/"/g, ''));
          const sectorValue = columns[sectorColumnIndex];

          if (sectorValue && sectorValue !== '') {
            industries.add(sectorValue);
          }
        }
      } else {
        // Fallback: look for industry code patterns in all columns
        console.log("⚠️ sector_unique_id column not found, using pattern matching fallback");
        for (let i = 1; i < lines.length; i++) {
          const line = lines[i].trim();
          if (!line) continue;

          const columns = line.split(',').map(col => col.trim().replace(/"/g, ''));

          columns.forEach(col => {
            if (/^[A-Z]?\d{2,4}$/.test(col) || /^[A-Z]$/.test(col)) {
              industries.add(col);
            }
          });
        }
      }

      return Array.from(industries);
    } catch (error) {
      console.error("❌ Failed to parse industries from CSV:", error);
      return [];
    }
  };

  const handleInfoFileChange = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    console.log("📁 File selected:", file?.name, file?.type, file?.size);

    if (file && (file.type === 'text/csv' || file.name.endsWith('.csv'))) {
      setInfoFile(file);

      // Update the store with the file
      const { setInfoFile: setStoreInfoFile } = useClusteringStore.getState();
      setStoreInfoFile(file);

      // Parse available industries from the CSV
      const industries = await parseIndustriesFromCSV(file);
      setAvailableIndustries(industries);

      console.log("✅ Info file uploaded to store:", file.name);
      console.log("📊 Found industries in CSV:", industries.length, industries.slice(0, 10));

      toast({
        title: "File Uploaded",
        description: `${file.name} uploaded. Found ${industries.length} industry codes.`,
        variant: "default",
      });
    } else {
      setInfoFile(null);
      setAvailableIndustries([]);
      console.log("❌ Invalid file type or no file selected");
    }
  };

  const downloadInputJson = (data: { lambda: number; k: number; pca_dim: number; level_value: string[] }) => {
    const inputJson = {
      pca_dim: data.pca_dim,
      lambda: data.lambda,
      k: data.k,
      level_value: data.level_value
    };

    const blob = new Blob([JSON.stringify(inputJson, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'clustering-input.json';
    a.click();
    URL.revokeObjectURL(url);

    toast({
      title: "Input JSON Downloaded",
      description: "Clustering input parameters have been downloaded as JSON",
      variant: "default",
    });
  };

  const downloadOutputJson = () => {
    if (!results?.clusterResult) {
      toast({
        title: "No Results Available",
        description: "Please run clustering first to generate output data",
        variant: "destructive",
      });
      return;
    }

    // Handle new API response format with companies array
    const outputJson = {
      best_k: results.clusterResult.best_k,
      companies: results.clusterResult.companies || [],
      // Legacy fields for backward compatibility
      dataset_id: results.clusterResult.dataset_id,
      embedding: results.clusterResult.embedding,
      labels: results.clusterResult.labels,
      level: results.clusterResult.level || 1,
      level_value: results.clusterResult.level_value,
      mode: results.clusterResult.mode || "subset",
      n_samples: results.clusterResult.n_samples,
      size: results.clusterResult.size
    };

    const blob = new Blob([JSON.stringify(outputJson, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'clustering-output.json';
    a.click();
    URL.revokeObjectURL(url);

    toast({
      title: "Output JSON Downloaded",
      description: "Clustering results have been downloaded as JSON",
      variant: "default",
    });
  };

  return (
    <div className="space-y-6">
      {/* Algorithm Parameters */}
      <div className="space-y-4">
        <h3 className="text-lg font-medium text-foreground">Algorithm Parameters</h3>

        <Form {...parametersForm}>
          <form onSubmit={parametersForm.handleSubmit(onParametersSubmit)} className="space-y-4">
            <FormField
              control={parametersForm.control}
              name="lambda"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Lambda (λ)</FormLabel>
                  <FormControl>
                    <Input
                      type="number"
                      step="any"
                      min="0"
                      max="100"
                      placeholder="0.5"
                      {...field}
                      onChange={(e) => field.onChange(parseFloat(e.target.value))}
                      data-testid="input-lambda"
                    />
                  </FormControl>
                  <p className="text-xs text-muted-foreground">
                    Weight factor for embedding vs info data
                  </p>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={parametersForm.control}
              name="k"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Number of Clusters (k)</FormLabel>
                  <div className="flex space-x-2">
                    <FormControl>
                      <Input
                        type="number"
                        min="2"
                        max="20"
                        placeholder="6"
                        {...field}
                        onChange={(e) => field.onChange(parseInt(e.target.value))}
                        className="flex-1"
                        data-testid="input-k"
                      />
                    </FormControl>
                    <Button
                      type="button"
                      variant="outline"
                      size="icon"
                      title="Auto-detect optimal k"
                      data-testid="button-auto-k"
                    >
                      ✨
                    </Button>
                  </div>
                  <p className="text-xs text-muted-foreground">
                    Or use multiple values: [3,4,5,6,7,8]
                  </p>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={parametersForm.control}
              name="pca_dim"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>PCA Dimensions</FormLabel>
                  <FormControl>
                    <Input
                      type="number"
                      min="2"
                      max="512"
                      placeholder="128"
                      {...field}
                      onChange={(e) => field.onChange(parseInt(e.target.value))}
                      data-testid="input-pca-dim"
                    />
                  </FormControl>
                  <p className="text-xs text-muted-foreground">
                    Number of PCA dimensions (2-512)
                  </p>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={parametersForm.control}
              name="level_value"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Mã ngành</FormLabel>
                  <FormControl>
                    <IndustrySelector
                      value={field.value}
                      onValueChange={field.onChange}
                      placeholder="Chọn mã ngành..."
                      data-testid="select-level-value"
                      availableIndustries={availableIndustries}
                      showOnlyAvailable={showOnlyAvailable}
                      onShowOnlyAvailableChange={setShowOnlyAvailable}
                    />
                  </FormControl>
                  <p className="text-xs text-muted-foreground">
                    Chọn mã ngành để phân tích
                  </p>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="space-y-2">
              <Label htmlFor="info-file">Info CSV File (Optional)</Label>
              <Input
                id="info-file"
                type="file"
                accept=".csv"
                onChange={handleInfoFileChange}
                data-testid="input-info-file"
              />
              <p className="text-xs text-muted-foreground">
                Upload CSV file with company information (optional)
              </p>
            </div>

            <div className="flex gap-2">
              <Button type="submit" className="flex-1" data-testid="button-update-parameters">
                Update Parameters
              </Button>
              <Button 
                type="button" 
                variant="outline" 
                onClick={() => downloadInputJson(parametersForm.getValues())}
                className="flex items-center gap-2"
                data-testid="button-download-input"
              >
                <Download className="h-4 w-4" />
                Input JSON
              </Button>
            </div>
          </form>
        </Form>
      </div>

      <Separator />

      {/* API Configuration */}
      <div className="space-y-4">
        <h3 className="text-lg font-medium text-foreground">API Configuration</h3>

        <Form {...apiForm}>
          <form onSubmit={apiForm.handleSubmit(onApiConfigSubmit)} className="space-y-4">
            <FormField
              control={apiForm.control}
              name="endpoint"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Endpoint URL</FormLabel>
                  <FormControl>
                    <Input
                      type="url"
                      placeholder="https://api.clustering-service.com"
                      {...field}
                      onBlur={field.onBlur}
                      data-testid="input-endpoint"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            {/* Connection Status */}
            <div className="flex items-center space-x-2">
              {connectionStatus === "connected" ? (
                <>
                  <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse" />
                  <Wifi className="h-4 w-4 text-green-600" />
                  <span className="text-sm text-green-600">Connected</span>
                </>
              ) : connectionStatus === "checking" ? (
                <>
                  <div className="w-2 h-2 bg-yellow-500 rounded-full animate-pulse" />
                  <span className="text-sm text-yellow-600">Checking connection...</span>
                </>
              ) : (
                <>
                  <div className="w-2 h-2 bg-gray-400 rounded-full" />
                  <WifiOff className="h-4 w-4 text-gray-400" />
                  <span className="text-sm text-gray-600">Disconnected</span>
                </>
              )}
            </div>

            <div className="flex gap-2">
              <Button type="submit" className="flex-1" data-testid="button-update-config">
                Update Configuration
              </Button>
              <Button 
                type="button" 
                variant="outline" 
                onClick={downloadOutputJson}
                className="flex items-center gap-2"
                data-testid="button-download-output"
                disabled={!results?.clusterResult}
              >
                <Download className="h-4 w-4" />
                Output JSON
              </Button>
            </div>
          </form>
        </Form>
      </div>
    </div>
  );
}

client/src/components/file-upload-zone.t.content:
import { useCallback, useRef } from "react";
import { useDropzone } from "react-dropzone";
import { useClusteringStore } from "@/lib/clustering-store";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { CloudUpload, Database, FileText, CheckCircle } from "lucide-react";

interface FileUploadZoneProps {
  type: "embeddings" | "info";
  title: string;
  description: string;
  icon: string;
  "data-testid": string;
}

export default function FileUploadZone({ type, title, description, icon, "data-testid": testId }: FileUploadZoneProps) {
  // Don't render anything for embeddings type
  if (type === "embeddings") {
    return null;
  }

  const { embeddingsFile, infoFile, uploadFile, fileMetadata } = useClusteringStore();
  const fileInputRef = useRef<HTMLInputElement | null>(null);

  const currentFile = infoFile;
  const currentMetadata = currentFile ? fileMetadata[currentFile.name] : null;

  const onDrop = useCallback((acceptedFiles: File[]) => {
    if (acceptedFiles.length > 0) {
      uploadFile(acceptedFiles[0], type);
    }
  }, [type, uploadFile]);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'text/csv': ['.csv'],
      'text/plain': ['.txt'],
    },
    maxFiles: 1,
  });

  const IconComponent = icon === "cloud-upload" ? CloudUpload : Database;

  return (
    <div className="space-y-2">
      <Card
        {...getRootProps()}
        className={`p-6 text-center cursor-pointer transition-colors border-2 border-dashed ${
          isDragActive 
            ? 'border-primary bg-blue-50' 
            : currentFile 
              ? 'border-green-300 bg-green-50'
              : 'border-muted-foreground/25 hover:border-primary'
        }`}
        data-testid={testId}
      >
        <input {...getInputProps()} />
        <IconComponent className={`h-8 w-8 mx-auto mb-3 ${
          currentFile ? 'text-green-500' : 'text-muted-foreground'
        }`} />
        <p className="text-sm font-medium text-foreground mb-1">{title}</p>
        <p className="text-xs text-muted-foreground mb-1">{description}</p>
        <p className="text-xs text-orange-600 mb-3">Tùy chọn - có thể bỏ trống</p>
        {currentFile ? (
          <div className="space-y-2">
            <div className="flex items-center justify-center space-x-2">
              <FileText className="h-4 w-4 text-green-500" />
              <span className="text-sm text-green-700 font-medium">{currentFile.name}</span>
            </div>
            <Button
              variant="outline"
              size="sm"
              className="text-primary border-primary hover:bg-primary hover:text-white"
              data-testid={`button-change-${type}`}
            >
              Change File
            </Button>
          </div>
        ) : (
          <Button
            variant="default"
            size="sm"
            className="bg-primary hover:bg-primary/90"
            data-testid={`button-choose-${type}`}
          >
            Choose File
          </Button>
        )}
      </Card>

      {/* File Preview */}
      {currentMetadata && (
        <Card className="p-3 bg-muted/50">
          <div className="flex items-start space-x-2">
            <CheckCircle className="h-4 w-4 text-green-500 mt-0.5 flex-shrink-0" />
            <div className="flex-1 text-sm space-y-1">
              <div className="font-medium text-foreground">File validated</div>
              <div className="text-muted-foreground">
                {currentMetadata.rowCount.toLocaleString()} rows, {currentMetadata.columnCount} columns
              </div>
              <div className="text-muted-foreground">
                Delimiter: "{currentMetadata.delimiter}" | Header: {currentMetadata.hasHeader ? "Yes" : "No"}
              </div>
              {currentMetadata.numericColumns.length > 0 && (
                <div className="text-muted-foreground">
                  Numeric columns: {currentMetadata.numericColumns.length}
                </div>
              )}
            </div>
          </div>
        </Card>
      )}
    </div>
  );
}

client/src/components/industry-selector..content:
import { useState, useEffect, useMemo } from "react";
import { Check, ChevronsUpDown, Search } from "lucide-react";
import { Checkbox } from "@/components/ui/checkbox";
import { Label } from "@/components/ui/label";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import {
  Command,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
} from "@/components/ui/command";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { Badge } from "@/components/ui/badge";
import { getAllIndustries, getIndustriesByLevel, type IndustryLevel } from "@/lib/industry-parser";

interface IndustrySelectorProps {
  value?: string;
  onValueChange: (value: string) => void;
  placeholder?: string;
  "data-testid"?: string;
  availableIndustries?: string[];
  showOnlyAvailable?: boolean;
  onShowOnlyAvailableChange?: (checked: boolean) => void;
}

export default function IndustrySelector({ 
  value = [], 
  onValueChange, 
  placeholder = "Chọn mã ngành...",
  "data-testid": testId,
  availableIndustries = [],
  showOnlyAvailable = false,
  onShowOnlyAvailableChange
}: IndustrySelectorProps) {
  const [open, setOpen] = useState(false);
  const [industries, setIndustries] = useState<IndustryLevel[]>([]);
  const [loading, setLoading] = useState(true);

  // Load embedded industry data
  useEffect(() => {
    try {
      const parsedIndustries = getAllIndustries();
      console.log('Loaded embedded industries:', parsedIndustries.length);
      setIndustries(parsedIndustries);
    } catch (error) {
      console.error('Failed to load embedded industry data:', error);
    } finally {
      setLoading(false);
    }
  }, []);

  // Filter industries based on availability
  const filteredIndustries = useMemo(() => {
    if (!showOnlyAvailable || availableIndustries.length === 0) {
      return industries;
    }
    return industries.filter(industry => 
      availableIndustries.includes(industry.apiCode) || 
      availableIndustries.includes(industry.code)
    );
  }, [industries, showOnlyAvailable, availableIndustries]);

  // Group industries by level for better display
  const industriesByLevel = useMemo(() => {
    return getIndustriesByLevel(filteredIndustries);
  }, [filteredIndustries]);

  const selectedIndustries = industries.filter(industry => value.includes(industry.apiCode));

  const getLevelLabel = (level: number) => {
    switch (level) {
      case 1: return "Level 1 - Ngành chính";
      case 2: return "Level 2 - Phân ngành";
      case 3: return "Level 3 - Nhóm";
      case 4: return "Level 4 - Lớp";
      case 5: return "Level 5 - Tiểu lớp";
      default: return `Level ${level}`;
    }
  };

  if (loading) {
    return (
      <Button
        variant="outline"
        role="combobox"
        disabled
        className="w-full justify-between"
        data-testid={testId}
      >
        Đang tải...
        <ChevronsUpDown className="ml-2 h-4 w-4 shrink-0 opacity-50" />
      </Button>
    );
  }

  return (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <Button
          variant="outline"
          role="combobox"
          aria-expanded={open}
          className="w-full justify-between"
          data-testid={testId}
        >
          {selectedIndustries.length > 0 ? (
            <div className="flex items-center space-x-2 truncate">
              <Badge variant="secondary" className="text-xs">
                {selectedIndustries.length} mã đã chọn
              </Badge>
              <span className="truncate">
                {selectedIndustries.map(ind => ind.apiCode).join(", ")}
              </span>
            </div>
          ) : (
            placeholder
          )}
          <ChevronsUpDown className="ml-2 h-4 w-4 shrink-0 opacity-50" />
        </Button>
      </PopoverTrigger>
      <PopoverContent className="w-[500px] p-0" align="start">
        <Command>
          <div className="border-b">
            <div className="flex items-center px-3">
              <Search className="mr-2 h-4 w-4 shrink-0 opacity-50" />
              <CommandInput
                placeholder="Tìm kiếm theo mã hoặc tên ngành..."
                className="flex h-11 w-full rounded-md bg-transparent py-3 text-sm outline-none placeholder:text-muted-foreground disabled:cursor-not-allowed disabled:opacity-50"
              />
            </div>

            {/* Availability filter checkbox inside dropdown */}
            {availableIndustries.length > 0 && onShowOnlyAvailableChange && (
              <div className="flex items-center space-x-2 px-3 py-2 border-t bg-muted/30">
                <Checkbox
                  id="show-only-available-dropdown"
                  checked={showOnlyAvailable}
                  onCheckedChange={onShowOnlyAvailableChange}
                />
                <Label htmlFor="show-only-available-dropdown" className="text-sm text-muted-foreground cursor-pointer">
                  Chỉ hiển thị mã ngành có trong danh sách ({availableIndustries.length} mã)
                </Label>
              </div>
            )}
          </div>
          <CommandList className="max-h-[300px]">
            <CommandEmpty>Không tìm thấy ngành nghề nào.</CommandEmpty>

            {Object.entries(industriesByLevel)
              .sort(([a], [b]) => parseInt(a) - parseInt(b))
              .map(([level, levelIndustries]) => (
                <CommandGroup key={level} heading={getLevelLabel(parseInt(level))}>
                  {levelIndustries.map((industry) => (
                    <CommandItem
                      key={industry.code}
                      value={`${industry.code} ${industry.name}`}
                      onSelect={() => {
                        const isSelected = value.includes(industry.apiCode);
                        const newValue = isSelected 
                          ? value.filter(code => code !== industry.apiCode)
                          : [...value, industry.apiCode];
                        onValueChange(newValue);
                      }}
                      className="flex items-center space-x-2"
                    >
                      <Checkbox
                        checked={value.includes(industry.apiCode)}
                        className="mr-2 h-4 w-4"
                      />
                      <Badge variant="outline" className="text-xs">
                        {industry.apiCode}
                      </Badge>
                      <span className="flex-1 truncate">{industry.name}</span>
                    </CommandItem>
                  ))}
                </CommandGroup>
              ))}
          </CommandList>
        </Command>
      </PopoverContent>
    </Popover>
  );
}

client/src/components/mock-test-panel.ts.content:
import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { generateMockClusterData, generateMockClusterDataVariants } from "../lib/mock-data";
import SimpleClusterVisualization from "./simple-cluster-visualization";
import { ClusterResult } from "../../../shared/schema";
import { Link } from "wouter";
import { ArrowLeft } from "lucide-react";

export default function MockTestPanel() {
  const [currentMockData, setCurrentMockData] = useState<ClusterResult | null>(null);
  const [selectedVariant, setSelectedVariant] = useState<string>("default");

  const loadMockData = (variant: string) => {
    setSelectedVariant(variant);

    switch (variant) {
      case "default":
        setCurrentMockData(generateMockClusterData());
        break;
      case "circular":
        setCurrentMockData(generateMockClusterDataVariants()[0]);
        break;
      case "linear":
        setCurrentMockData(generateMockClusterDataVariants()[1]);
        break;
      case "density":
        setCurrentMockData(generateMockClusterDataVariants()[2]);
        break;
      default:
        setCurrentMockData(generateMockClusterData());
    }
  };

  const variants = [
    { key: "default", name: "Standard Clusters", description: "4 clusters with random scatter" },
    { key: "circular", name: "Circular Pattern", description: "3 clusters in circular arrangement" },
    { key: "linear", name: "Linear Pattern", description: "4 clusters along different slopes" },
    { key: "density", name: "Density Varied", description: "3 clusters with different densities" }
  ];

  return (
    <div className="p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold mb-2">Mock Data Test Panel</h2>
          <p className="text-muted-foreground">Test cluster visualization with different mock data patterns</p>
        </div>
        <Link href="/">
          <Button variant="outline" className="flex items-center gap-2" data-testid="button-back-to-main">
            <ArrowLeft className="h-4 w-4" />
            Back to Main
          </Button>
        </Link>
      </div>

      {/* Mock Data Controls */}
      <Card>
        <CardHeader>
          <CardTitle className="text-lg">Mock Data Variants</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
            {variants.map((variant) => (
              <Button
                key={variant.key}
                variant={selectedVariant === variant.key ? "default" : "outline"}
                className="h-auto p-4 flex flex-col items-start space-y-2"
                onClick={() => loadMockData(variant.key)}
              >
                <div className="font-medium">{variant.name}</div>
                <div className="text-sm text-muted-foreground text-left">
                  {variant.description}
                </div>
              </Button>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* Current Data Info */}
      {currentMockData && (
        <Card>
          <CardHeader>
            <CardTitle className="text-lg">Current Dataset Info</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
              <div>
                <div className="font-medium">Dataset ID</div>
                <Badge variant="secondary">{currentMockData.dataset_id}</Badge>
              </div>
              <div>
                <div className="font-medium">Samples</div>
                <Badge variant="secondary">{currentMockData.n_samples}</Badge>
              </div>
              <div>
                <div className="font-medium">Clusters</div>
                <Badge variant="secondary">{currentMockData.best_k}</Badge>
              </div>
              <div>
                <div className="font-medium">Lambda</div>
                <Badge variant="secondary">{currentMockData.lambda}</Badge>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Visualization */}
      {currentMockData && (
        <Card>
          <CardHeader>
            <CardTitle className="text-lg">Cluster Visualization</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex justify-center">
              <SimpleClusterVisualization 
                clusterResult={currentMockData}
                width={800}
                height={600}
              />
            </div>
          </CardContent>
        </Card>
      )}

      {/* Instructions */}
      {!currentMockData && (
        <Card>
          <CardContent className="text-center py-12">
            <div className="text-muted-foreground mb-4">
              Select a mock data variant above to test the cluster visualization
            </div>
            <Button onClick={() => loadMockData("default")}>
              Load Default Mock Data
            </Button>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/results-panel.tsx.content:
import { useClusteringStore } from "../lib/clustering-store";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Separator } from "@/components/ui/separator";
import { Badge } from "@/components/ui/badge";
import { BarChart3, TrendingUp, ExternalLink } from "lucide-react";
import SimpleClusterVisualization from "./simple-cluster-visualization";

export default function ResultsPanel() {
  const { results, logs, setSelectedProjectionType } = useClusteringStore();

  if (!results) {
    return (
      <div className="w-full h-full bg-card p-4 overflow-hidden">
        <h3 className="text-lg font-semibold text-foreground mb-4">Analysis Results</h3>
        <div className="text-center text-muted-foreground">
          <div className="mb-3">📈</div>
          <p className="text-sm">Run clustering analysis to see results</p>
        </div>
      </div>
    );
  }

  const clusters = results.metrics?.clusters || [];

  return (
    <div className="w-full h-full bg-card flex flex-col overflow-hidden">
      <ScrollArea className="flex-1">
        <div className="p-4 space-y-4">
          <h3 className="text-lg font-semibold text-foreground">Analysis Results</h3>

          {/* Metrics Summary */}
          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-sm">Clustering Metrics</CardTitle>
            </CardHeader>
            <CardContent className="space-y-2 text-sm">
              <div className="flex justify-between">
                <span className="text-muted-foreground">Best k:</span>
                <Badge variant="secondary" data-testid="metric-best-k">
                  {results.clusterResult?.best_k || 'N/A'}
                </Badge>
              </div>
              <div className="flex justify-between">
                <span className="text-muted-foreground">Silhouette Score:</span>
                <span className="font-medium" data-testid="metric-silhouette">
                  {results.metrics?.silhouetteScore?.toFixed(3) || 'N/A'}
                </span>
              </div>
              <div className="flex justify-between">
                <span className="text-muted-foreground">Inertia:</span>
                <span className="font-medium" data-testid="metric-inertia">
                  {results.metrics?.inertia?.toFixed(2) || 'N/A'}
                </span>
              </div>
              <div className="flex justify-between">
                <span className="text-muted-foreground">Lambda used:</span>
                <span className="font-medium" data-testid="metric-lambda">
                  {results.clusterResult?.lambda || 'N/A'}
                </span>
              </div>
            </CardContent>
          </Card>

          {/* Cluster Visualization */}
          {results.clusterResult && results.clusterResult.embedding && results.clusterResult.labels && results.clusterResult.size && (
            <div className="space-y-3">
              <h4 className="text-sm font-medium text-foreground">Interactive Visualization</h4>
              <Card className="overflow-hidden">
                <CardContent className="p-0">
                  <SimpleClusterVisualization 
                    clusterResult={results.clusterResult}
                    width={240}
                    height={180}
                  />
                </CardContent>
              </Card>
            </div>
          )}

          {/* Projection Plots */}
          {(results as any).projectionImages && Object.keys((results as any).projectionImages).length > 0 && (
            <div className="space-y-3">
              <h4 className="text-sm font-medium text-foreground">Projection Plots</h4>

              {Object.entries((results as any).projectionImages).map(([plotType, imageUrl]: [string, any]) => (
                <Card 
                  key={plotType} 
                  className="overflow-hidden cursor-pointer hover:ring-2 hover:ring-primary transition-all" 
                  data-testid={`projection-${plotType}`}
                  onClick={() => setSelectedProjectionType(plotType as "pca" | "tsne" | "umap")}
                >
                  <CardHeader className="pb-2">
                    <CardTitle className="text-sm capitalize">{plotType.toUpperCase()} Projection</CardTitle>
                  </CardHeader>
                  <CardContent className="p-0">
                    <img 
                      src={imageUrl as string} 
                      alt={`${plotType} projection`}
                      className="w-full h-auto max-h-48 object-contain bg-white"
                      onError={(e) => {
                        console.error(`Failed to load ${plotType} image:`, e);
                        e.currentTarget.style.display = 'none';
                      }}
                    />
                  </CardContent>
                </Card>
              ))}
            </div>
          )}

          {/* Metric Plots */}
          {(results as any).metricImages && Object.keys((results as any).metricImages).length > 0 && (
            <div className="space-y-3">
              <h4 className="text-sm font-medium text-foreground">Metric Plots</h4>

              {Object.entries((results as any).metricImages).map(([metricType, imageUrl]: [string, any]) => (
                <Card key={metricType} className="overflow-hidden" data-testid={`metric-${metricType}`}>
                  <CardHeader className="pb-2">
                    <CardTitle className="text-sm capitalize">{metricType.replace('_', ' ')}</CardTitle>
                  </CardHeader>
                  <CardContent className="p-0">
                    <img 
                      src={imageUrl as string} 
                      alt={`${metricType} plot`}
                      className="w-full h-auto max-h-48 object-contain bg-white"
                      onError={(e) => {
                        console.error(`Failed to load ${metricType} image:`, e);
                        e.currentTarget.style.display = 'none';
                      }}
                    />
                  </CardContent>
                </Card>
              ))}
            </div>
          )}

          <Separator />

          {/* Cluster Details */}
          <div className="space-y-3">
            <h4 className="text-sm font-medium text-foreground">Cluster Breakdown</h4>

            {clusters.slice(0, 3).map((cluster, index) => {
              const colors = ['bg-blue-500', 'bg-green-500', 'bg-red-500', 'bg-yellow-500', 'bg-purple-500', 'bg-orange-500'];
              return (
                <Card key={cluster.id} data-testid={`cluster-${cluster.id}`}>
                  <CardContent className="p-3">
                    <div className="flex items-center justify-between mb-2">
                      <div className="flex items-center space-x-2">
                        <div className={`w-3 h-3 ${colors[index]} rounded-full`} />
                        <span className="text-sm font-medium">Cluster {cluster.id}</span>
                      </div>
                      <span className="text-xs text-muted-foreground">{cluster.size} points</span>
                    </div>
                    <div className="text-xs text-muted-foreground space-y-1">
                      <div>Centroid: ({cluster.centroid.x.toFixed(2)}, {cluster.centroid.y.toFixed(2)})</div>
                      <div>Avg distance: {cluster.avgDistance.toFixed(2)}</div>
                    </div>
                  </CardContent>
                </Card>
              );
            })}

            {clusters.length > 3 && (
              <Button
                variant="outline"
                className="w-full text-sm"
                data-testid="button-show-all-clusters"
              >
                Show all {clusters.length} clusters
              </Button>
            )}
          </div>

          <Separator />

          {/* Processing Log */}
          <div className="space-y-3">
            <h4 className="text-sm font-medium text-foreground">Processing Log</h4>
            <Card>
              <CardContent className="p-3">
                <ScrollArea className="h-32">
                  <div className="text-xs font-mono space-y-1" data-testid="processing-log">
                    {logs.map((log, index) => (
                      <div key={index} className={`${
                        log.type === 'success' ? 'text-green-600' :
                        log.type === 'error' ? 'text-red-600' :
                        log.type === 'info' ? 'text-blue-600' :
                        'text-muted-foreground'
                      }`}>
                        [{log.type === 'success' ? '✓' : 
                          log.type === 'error' ? '✗' : 
                          log.type === 'info' ? 'i' : '·'}] {log.message}
                      </div>
                    ))}
                  </div>
                </ScrollArea>
              </CardContent>
            </Card>
          </div>
        </div>
      </ScrollArea>
    </div>
  );
}

